LoadImplementation.common:CreateProcedureSQL:

create procedure dbo.USP_DATAFORMTEMPLATE_EDITLOAD_FOOD_BANK_TRANSACTION_DETAIL_LHOLT
(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
    @FOODITEMID uniqueidentifier = null output,
    @FOODITEMAMOUNT money = null output,
    @QUANTITY smallint = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
        @FOODITEMID = FOODITEMID, 
        @FOODITEMAMOUNT = FOODITEMAMOUNT, 
        @QUANTITY = QUANTITY
	from dbo.USR_FOODBANKTXDETAIL_LHOLT
	where ID = @ID

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USP_DATAFORMTEMPLATE_EDIT_FOOD_BANK_TRANSACTION_DETAIL_LHOLT ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @FOODITEMID uniqueidentifier, @FOODITEMAMOUNT money, @QUANTITY smallint ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
begin try -- handle updating the data update dbo.USR_FOODBANKTXDETAIL_LHOLT set FOODITEMID = @FOODITEMID, FOODITEMAMOUNT = @FOODITEMAMOUNT, QUANTITY = @QUANTITY, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = getdate() where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
